mapper.select:
select

from record


    cname like concat('%',#{cname,jdbcType=VARCHAR},'%')



    and rnumber = #{rnumber,jdbcType=INTEGER}



    and useable = #{useable,jdbcType=INTEGER}

select

from record


    cid = #{cid,jdbcType=INTEGER}


    and rnumber = #{rnumber,jdbcType=INTEGER}


    and useable = #{useable,jdbcType=INTEGER}
